#!/usr/bin/python
"""
Project Euler
Problem 8
Find the greatest product of five consecutive digits in the 
1000-digit number.
Example: 
>>> p8("123456789")
15120
"""

def p8(s=""):
	slist = list(s)
	maxN = 0
	for sl in xrange(len(slist)):
		slist[sl] = int(slist[sl])
		if sl > 3:
			test = slist[sl-4] * slist[sl-3] * slist[sl-2] * slist[sl-1] * slist[sl]
			if test > maxN:
				maxN = test
	return maxN
	
if __name__ == '__main__':
	import doctest
	doctest.testmod()
	p8()